//form show output on console using mui 
import { Button, Checkbox, FormControl, FormControlLabel, FormGroup, FormLabel, MenuItem, Radio, RadioGroup, Select, TextField, Typography } from "@mui/material";
import { useState } from "react";

const data = {
    name: "",
    email: "",
    password: '',
    courses: '',
    terms: false
}

function Form() {
    const [input, setInput] = useState(data);

    const fieldChange = (e) => {
        setInput((prev) => ({
            ...prev,
            [e.target.name]: [e.target.value]  //updating the state with new val

        }));
    };

    const clickbtn = (e) => {
        e.preventDefault();
        console.log(input)
    }

    return (
        <>
            <Typography variant='h4' m={2} sx={{ color: 'green', textAlign: 'center' }}> form with dropdown </Typography>

            <FormControl>
                <TextField
                    name="name"
                    type='text'
                    label='name'
                    sx={{ margin: '20px' }}
                    value={input.name}
                    onChange={fieldChange}


                />
                <TextField
                    name="email"
                    type='email'
                    label='email'
                    sx={{ margin: '20px' }}
                    value={input.email}
                    onChange={fieldChange}

                />
                <TextField
                    name='password'
                    type="password"
                    label='password'
                    sx={{ margin: '20px' }}
                    value={input.password}
                    onChange={fieldChange}

                />

                {/* dropdown  */}

                <Select
                    name="courses"
                    value={input.courses}
                    onChange={fieldChange}
                    label='courses'

                >
                    <MenuItem value='html/css'>html/css</MenuItem>
                    <MenuItem value='javascript'>javascript</MenuItem>
                    <MenuItem value='reactjs'>reactjs</MenuItem>
                    <MenuItem value='expressjs'>expressjs</MenuItem>
                    <MenuItem value='nodejs'>nodejs</MenuItem>
                    <MenuItem value='mysql'>mysql</MenuItem>
                    <MenuItem value='mongodb'>mongodb</MenuItem>
                </Select>
                <br />

                {/* Checkbox  */}

                <FormGroup>
                    <FormControlLabel
                        label='I Agree T&C'
                        control={
                            <Checkbox onClick={() => {
                                setInput((prev) => ({
                                    ...prev,
                                    terms: !input.terms
                                }))
                            }} />} />
                </FormGroup>

 
                {/* Checkbox  */}

                <FormLabel>Gender</FormLabel>
                <RadioGroup name="gender" row>
                    <FormControlLabel value='male' label='Male' control={<Radio/>}/>                      
                    <FormControlLabel value='female' label='Female' control={<Radio/>}/>
                    <FormControlLabel value='other' label='other' control={<Radio/>}/>
                </RadioGroup>


                <Button variant="contained" onClick={clickbtn}>Submit</Button>
  
            </FormControl>


        </>
    )
}

export default Form;
