//form show output on console using mui 
import { Button, FormControl, MenuItem, Select, TextField } from "@mui/material";
import { useState } from "react";

const data={
    name:"",
    email:"",
    password:'',
    courses:''
}

function Form() {
    const [input,setInput]=useState(data);

    const fieldChange=(e)=>{
        setInput((prev)=>({
            ...prev,
            [e.target.name]:[ e.target.value]  //updating the state with new val

        }));
    };

    const clickbtn=(e)=>{
        e.preventDefault();
        console.log(input)
    }

  return (
    <>
    <FormControl>
        <TextField 
            name="name"
            type='text' 
            label='name' 
            sx={{margin:'20px'}}
            value={input.name}
            onChange={fieldChange}
            
        />
        <TextField 
            name="email"
            type='email' 
            label='email'  
            sx={{margin:'20px'}} 
            value={input.email}
            onChange={fieldChange}
        />
        <TextField 
            name='password'
            type="password"
            label='password'  
            sx={{margin:'20px'}}
            value={input.password}
            onChange={fieldChange}
        />

        {/* dropdown  */}

        <Select label='courses' name="courses" value={input.courses}>
            <MenuItem value='html/css'>html/css</MenuItem>
            <MenuItem value='javascript'>javascript</MenuItem>
            <MenuItem value='reactjs'>reactjs</MenuItem>
            <MenuItem value='expressjs'>expressjs</MenuItem>
            <MenuItem value='nodejs'>nodejs</MenuItem>
            <MenuItem value='mysql'>mysql</MenuItem>
            <MenuItem value='mongodb'>mongodb</MenuItem>
        </Select>
        <br/>
        <Button variant="contained" onClick={clickbtn}>Submit</Button>

    </FormControl>
     

    </>
  )
}

export default Form;
