//form show output on console using mui 
import { Button, FormControl, TextField } from "@mui/material";
import { useState } from "react";

const data={
    name:"",
    email:"",
    password:''
}

function Form() {
    const [input,setInput]=useState(data);

    const fieldChange=(e)=>{
        setInput((prev)=>({
            ...prev,
            [e.target.name]:[ e.target.value]  //updating the state with new val

        }));
    };

    const clickbtn=(e)=>{
        e.preventDefault();
        console.log(input)
    }

  return (
    <>
    <FormControl>
        <TextField 
            name="name"
            type='text' 
            placeholder='name' 
            sx={{margin:'20px'}}
            value={input.name}
            onChange={fieldChange}
            
        />
        <TextField 
            name="email"
            type='email' 
            placeholder='email'  
            sx={{margin:'20px'}} 
            value={input.email}
            onChange={fieldChange}
        />
        <TextField 
            name='password'
            type="password"
            placeholder='password'  
            sx={{margin:'20px'}}
            value={input.password}
            onChange={fieldChange}
        />
        <Button variant="contained" onClick={clickbtn}>Submit</Button>

    </FormControl>
      
    </>
  )
}

export default Form;
